function Ball(selfObjet) {
    if (selfObjet) {
        this.height = selfObjet.height;
        this.weight = selfObjet.weight;
        this.color = selfObjet.color;
        this.name = selfObjet.name;
    }
    else {
        this.height = "168";
        this.weight = "62";
        this.color = "red";
        this.name = "luomingzhong";
    }
}
Ball.prototype.setHeight = function (height) {
    this.height = height;
};

Ball.prototype.setWeight = function (weight) {
    this.weight = weight;
};

Ball.prototype.getHeight = function () {
    return this.height;
};

module.exports = Ball;
